import React, { useState, useEffect, useMemo } from 'react';
import { useQuery } from '@tanstack/react-query';

interface TravelPost {
  id: string;
  title: string;
  content: string;
  images: string[];
  location: {
    lat: number;
    lng: number;
    name: string;
  };
}

export const TravelPostCard: React.FC<{ postId: string }> = ({ postId }) => {
  const { data: post, isLoading, error } = useQuery({
    queryKey: ['travel-post', postId],
    queryFn: () => fetchTravelPost(postId),
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  const formattedLocation = useMemo(() => {
    if (!post?.location) return '';
    return `${post.location.name} (${post.location.lat.toFixed(2)}, ${post.location.lng.toFixed(2)})`;
  }, [post?.location]);

  if (isLoading) return Loading...;
  if (error) return Error loading post;
  if (!post) return null;

  return (


        {post.title}
        {formattedLocation}

          {post.content}



  );
};




