 Recommended: Zustand
For Vibe-Curator, Zustand is recommended due to its simplicity, small bundle size, and excellent TypeScript support.


import { create } from 'zustand';
import { persist } from 'zustand/middleware';

interface TravelStore {
  posts: TravelPost[];
  favorites: string[];
  addPost: (post: TravelPost) => void;
  toggleFavorite: (postId: string) => void;
  searchQuery: string;
  setSearchQuery: (query: string) => void;
}

export const useTravelStore = create()(
  persist(
    (set, get) => ({
      posts: [],
      favorites: [],
      searchQuery: '',

      addPost: (post) => 
        set((state) => ({ posts: [...state.posts, post] })),

      toggleFavorite: (postId) =>
        set((state) => ({
          favorites: state.favorites.includes(postId)
            ? state.favorites.filter(id => id !== postId)
            : [...state.favorites, postId]
        })),

      setSearchQuery: (query) => set({ searchQuery: query }),
    }),
    {
      name: 'travel-storage',
      partialize: (state) => ({ 
        favorites: state.favorites,
        searchQuery: state.searchQuery 
      }),
    }
  )
);

