Offline-First Architecture with IndexedD:
Offline Storage Strategy


 IndexedDB
Primary offline storage for travel posts, images, and user data


 Memory Cache
In-memory caching for frequently accessed data


 Service Worker
Network request caching and background sync



IndexedDB Implementation

// db/travelDB.ts
import Dexie, { Table } from 'dexie';

export interface TravelPost {
  id?: number;
  title: string;
  content: string;
  images: string[];
  location: {
    lat: number;
    lng: number;
    name: string;
  };
  createdAt: Date;
  syncStatus: 'synced' | 'pending' | 'failed';
}

export class TravelDB extends Dexie {
  posts!: Table;
  images!: Table<{ id?: number; postId: number; blob: Blob; url: string }>;

  constructor() {
    super('TravelDB');
    this.version(1).stores({
      posts: '++id, title, createdAt, syncStatus',
      images: '++id, postId, url'
    });
  }
}

export const db = new TravelDB();

// Service for offline operations
export class OfflineTravelService {
  async saveTravelPost(post: Omit) {
    const id = await db.posts.add({
      ...post,
      syncStatus: 'pending'
    });

    // Schedule background sync
    if ('serviceWorker' in navigator && 'sync' in window.ServiceWorkerRegistration.prototype) {
      const registration = await navigator.serviceWorker.ready;
      await registration.sync.register('sync-travel-posts');
    }

    return id;
  }

  async getTravelPosts(limit = 20, offset = 0) {
    return await db.posts
      .orderBy('createdAt')
      .reverse()
      .offset(offset)
      .limit(limit)
      .toArray();
  }

  async searchPosts(query: string) {
    return await db.posts
      .filter(post => 
        post.title.toLowerCase().includes(query.toLowerCase()) ||
        post.content.toLowerCase().includes(query.toLowerCase())
      )
      .toArray();
  }
}

