 Recommended: Fastify
Fastify delivers approximately 78x more requests per second than Express, making it ideal for high-performance applications like Vibe-Curator.

Fastify Server Setup:

// server.js
import Fastify from 'fastify';
import cors from '@fastify/cors';
import helmet from '@fastify/helmet';
import rateLimit from '@fastify/rate-limit';

const fastify = Fastify({
  logger: {
    level: process.env.LOG_LEVEL || 'info',
    transport: {
      target: 'pino-pretty',
      options: {
        colorize: true
      }
    }
  }
});

// Security plugins
await fastify.register(helmet, {
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'"],
      scriptSrc: ["'self'"],
      imgSrc: ["'self'", "data:", "https:"]
    }
  }
});

await fastify.register(cors, {
  origin: process.env.ALLOWED_ORIGINS?.split(',') || ['http://localhost:3000']
});

await fastify.register(rateLimit, {
  max: 100,
  timeWindow: '1 minute'
});

// Travel posts routes
fastify.register(async function (fastify) {
  fastify.get('/api/travel-posts', async (request, reply) => {
    const { page = 1, limit = 20, search } = request.query;

    try {
      const posts = await getTravelPosts({
        page: parseInt(page),
        limit: parseInt(limit),
        search
      });

      return {
        success: true,
        data: posts,
        pagination: {
          page: parseInt(page),
          limit: parseInt(limit),
          total: posts.total
        }
      };
    } catch (error) {
      fastify.log.error(error);
      reply.code(500).send({
        success: false,
        error: 'Failed to fetch travel posts'
      });
    }
  });
});

const start = async () => {
  try {
    await fastify.listen({ 
      port: process.env.PORT || 3001,
      host: '0.0.0.0'
    });
    fastify.log.info('Server listening on port 3001');
  } catch (err) {
    fastify.log.error(err);
    process.exit(1);
  }
};

start();

