// hooks/useAPI.ts
import { useState, useEffect } from 'react';
import { useTravelStore } from '../store/travelStore';

interface APIOptions {
  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
  body?: any;
  headers?: Record;
  cache?: boolean;
  offline?: boolean;
}

export function useAPI(endpoint: string, options: APIOptions = {}) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isOnline } = useTravelStore();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Check offline mode
        if (!isOnline && options.offline) {
          const cachedData = await getCachedData(endpoint);
          if (cachedData) {
            setData(cachedData);
            return;
          }
        }

        const response = await fetch(`${process.env.REACT_APP_API_URL}${endpoint}`, {
          method: options.method || 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${getAuthToken()}`,
            ...options.headers
          },
          body: options.body ? JSON.stringify(options.body) : undefined
        });

        if (!response.ok) {
          throw new Error(`API Error: ${response.status} ${response.statusText}`);
        }

        const result = await response.json();
        setData(result.data);

        // Cache successful responses
        if (options.cache && response.ok) {
          await cacheData(endpoint, result.data);
        }

      } catch (err) {
        const errorMessage = err instanceof Error ? err.message : 'Unknown error';
        setError(errorMessage);

        // Try to load cached data on error
        if (options.offline) {
          const cachedData = await getCachedData(endpoint);
          if (cachedData) {
            setData(cachedData);
            setError(`Using cached data: ${errorMessage}`);
          }
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [endpoint, options.method, JSON.stringify(options.body)]);

  return { data, loading, error, refetch: () => fetchData() };
}

// Utility functions
async function getCachedData(key: string): Promise {
  try {
    const cached = localStorage.getItem(`api_cache_${key}`);
    if (cached) {
      const { data, timestamp } = JSON.parse(cached);
      const isExpired = Date.now() - timestamp > 5 * 60 * 1000; // 5 minutes
      if (!isExpired) {
        return data;
      }
    }
  } catch (error) {
    console.error('Cache retrieval error:', error);
  }
  return null;
}

async function cacheData(key: string, data: T): Promise {
  try {
    const cacheEntry = {
      data,
      timestamp: Date.now()
    };
    localStorage.setItem(`api_cache_${key}`, JSON.stringify(cacheEntry));
  } catch (error) {
    console.error('Cache storage error:', error);
  }
}

function getAuthToken(): string {
  return localStorage.getItem('auth_token') || '';
}

Error Handling Patterns




Frontend Error Boundaries


// components/ErrorBoundary.tsx
import React, { Component, ErrorInfo, ReactNode } from 'react';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
}

interface State {
  hasError: boolean;
  error?: Error;
}

export class ErrorBoundary extends Component {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): State {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    console.error('Error caught by boundary:', error, errorInfo);

    // Send error to monitoring service
    this.reportError(error, errorInfo);
  }

  reportError(error: Error, errorInfo: ErrorInfo) {
    // Integration with error monitoring service
    if (process.env.NODE_ENV === 'production') {
      // Send to Sentry, LogRocket, etc.
    }
  }

  render() {
    if (this.state.hasError) {
      return this.props.fallback || (

          Something went wrong
          We're sorry, but something unexpected happened.
           window.location.reload()}>
            Reload Page


      );
    }

    return this.props.children;
  }
}




Backend Error Middleware


// middleware/errorHandler.js
export const errorHandler = (error, request, reply) => {
  const { statusCode = 500, message } = error;

  // Log error details
  request.log.error({
    error: error.message,
    stack: error.stack,
    url: request.url,
    method: request.method,
    headers: request.headers,
    body: request.body
  });

  // Determine error response based on environment
  const errorResponse = {
    success: false,
    error: {
      message: statusCode < 500 ? message : 'Internal server error',
      code: error.code || 'UNKNOWN_ERROR',
      timestamp: new Date().toISOString(),
      requestId: request.id
    }
  };

  // Add stack trace in development
  if (process.env.NODE_ENV === 'development') {
    errorResponse.error.stack = error.stack;
  }

  reply.code(statusCode).send(errorResponse);
};

// Custom error classes
export class ValidationError extends Error {
  constructor(message, field) {
    super(message);
    this.name = 'ValidationError';
    this.statusCode = 422;
    this.field = field;
  }
}

export class NotFoundError extends Error {
  constructor(resource) {
    super(`${resource} not found`);
    this.name = 'NotFoundError';
    this.statusCode = 404;
  }
}

